INSERT INTO `ecs_mail_templates` VALUES ('19', 'email_validate', '1', '邮箱验证申请', '<div id=\"qm_con_body\">\r\n<div class=\"qmbox qm_con_body_content\" id=\"mailContentContainer\" style=\"\">\r\n<p style=\"margin:0px;padding:0px;\"><strong style=\"font-size:14px;line-height:24px;color:#333333;font-family:arial,sans-serif;\">亲爱的用户：</strong></p>\r\n<p style=\"margin:0px;padding:0px;line-height:24px;font-size:12px;color:#333333;font-family:\'宋体\',arial,sans-serif;\"><span style=\"font-size: larger;\">您好！</span><span style=\"font-size: larger;\"><br />\r\n</span></p>\r\n<p style=\"margin:0px;padding:0px;line-height:24px;font-size:12px;color:#333333;font-family:\'宋体\',arial,sans-serif;\"><span style=\"font-size: larger;\">您当前正在进行邮箱身份验证，请在页面的邮箱验证码输入框中输入此次验证码：</span></p>\r\n<p style=\"margin:0px;padding:0px;line-height:24px;font-size:12px;color:#333333;font-family:\'宋体\',arial,sans-serif;\"><span style=\"color: rgb(51, 102, 255);\"><span style=\"font-size: larger;\"><span style=\"border-bottom: 1px dashed rgb(204, 204, 204); z-index: 1;\">{$email_code}</span></span></span><span style=\"font-size: larger;\"> </span><span style=\"font-size: larger;\">验证码的有效时间为30分钟，请在有效时间内完成验证。</span></p>\r\n<p style=\"margin:0px;padding:0px;line-height:24px;font-size:12px;color:#333333;font-family:\'宋体\',arial,sans-serif;\"><span style=\"font-size: larger;\">如非本人操作，请忽略此邮件，由此给您带来的不便请谅解！</span><span style=\"font-size: medium;\"> </span></p>\r\n<p>{$shop_name}<br />\r\n<span style=\"border-bottom:1px dashed #ccc;\">{$send_date}</span></p>\r\n</div>\r\n</div>', '1438746507', '0', 'template');
